mapper.select:
select
/*按月*/
  TO_CHAR(PERIODDATE,'YYYY-MM') as periodDate,

/*按周*/
  TO_CHAR(PERIODDATE,'YYYY-MM') as periodDate,

/*按天*/
TO_CHAR(PERIODDATE,'dd') as periodDate,

sum(PLAN_OUTPUT_PNL_QTY) as plan,sum(ACTUAL_OUTPUT_PNL_QTY) as actual,FACTORY from DWS_PRODUCT_OUTPUT_FIDS

  WHERE PERIODDATE >= #{beginDate}
  and PERIODDATE <= #{endDate}


    AND PRODUCTTYPE=#{product,jdbcType=VARCHAR}

GROUP BY
    /*按月*/
    TO_CHAR(PERIODDATE,'YYYY-MM'),


        TO_CHAR(PERIODDATE,'YYYY-MM'),


        TO_CHAR(PERIODDATE,'dd'),

FACTORY

select
    TO_CHAR(PERIODDATE,'mm/dd') as periodDate,
sum(PLAN_OUTPUT_PNL_QTY) as plan,sum(ACTUAL_OUTPUT_PNL_QTY) as actual,FACTORY from DWS_PRODUCT_OUTPUT_FIDS

  WHERE PERIODDATE >= #{beginDate}
  and PERIODDATE <= #{endDate}


    AND PRODUCIDE=#{productId,jdbcType=VARCHAR}

GROUP BY
    TO_CHAR(PERIODDATE,'mm/dd'), FACTORY

select
TO_CHAR(PERIODDATE,'mon')  as periodDate,
sum(PLAN_OUTPUT_PNL_QTY) as plan,sum(ACTUAL_OUTPUT_PNL_QTY) as actual,FACTORY from DWS_PRODUCT_OUTPUT_FIDS

  WHERE PERIODDATE >= #{beginDate}
  and PERIODDATE <= #{endDate}


    AND PRODUCIDE=#{productId,jdbcType=VARCHAR}

GROUP BY
TO_CHAR(PERIODDATE,'mon'), FACTORY
