mapper.select:
SELECT
EQP_ID as eqpId,
EQP_STATUS as status,
sum(STATUS_DURATION) as statusNum
FROM
DWR_EQP_OEE_FIDS
WHERE
FACTORY=#{factory}
AND EQP_ID IN

    #{eqpId}


  and PERIODDATE >= #{beginDate}
  and PERIODDATE <= #{endDate}

GROUP BY EQP_ID,EQP_STATUS

SELECT FACTORY as factory, EQP_ID as eqpId, to_char(PERIODDATE,'HH24')||':00' as periodDate, EQP_STATUS as status, sum(STATUS_DURATION) as statusNum
FROM DWR_EQP_OEE_FIDS WHERE FACTORY=#{factory} and EQP_ID=#{eqpId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
GROUP BY FACTORY , EQP_ID , to_char(PERIODDATE,'HH24')||':00' , EQP_STATUS
