mapper.select:
SELECT
EQP_ID as eqpId,
EQP_STATUS as status,
STATUS_DURATION as statusNum
FROM
DWR_EQP_OEE_FIDS
WHERE
FACTORY=#{factory}
AND EQP_TYPE IN

    #{eqpId}


  and PERIODDATE >= #{beginDate}
  and PERIODDATE <= #{endDate}

GROUP BY EQP_ID,EQP_STATUS,STATUS_DURATION

SELECT FACTORY as factory, to_char(PERIODDATE,'HH24')||':00' as periodDate, EQP_STATUS as status, STATUS_DURATION as statusNum, EQP_ID as eqpId FROM DWR_EQP_OEE_FIDS WHERE FACTORY=#{factory} and EQP_ID=#{eqpId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
ORDER BY EQP_ID
