mapper.select:
select a.KEY, a.VAL, a.KEYDESC, a.DATATYPE, a.DEMODATA, a.DATADATE from DWR_EQUIPMENT_STATUS_FIDS a inner join ( select FACTORY as factory, max(DATADATE) as DATADATE, KEY as key, DATATYPE as dataType, DEMODATA as demoData from DWR_EQUIPMENT_STATUS_FIDS WHERE
FACTORY=#{factory,jdbcType=VARCHAR}
group by FACTORY,KEY,DATATYPE,DEMODATA ) b on a.FACTORY = b.FACTORY and a.DATADATE = b.DATADATE and a.KEY = b.KEY and a.DATATYPE = b.DATATYPE and a.DEMODATA = b.DEMODATA
