mapper.select:
select decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY) as factory, TO_CHAR(PERIODDATE,'HH24')||':00' as dataDate, sum(ACTUAL_OUTPUT_PNL_QTY) as amount from DWS_PRODUCT_OUTPUT_FIDS_H WHERE
PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
and FACTORY IN
#{factory}
GROUP BY decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY),TO_CHAR(PERIODDATE,'HH24')||':00'
