mapper.select:
select sum(NVL(CT_TARGET,0)) as plan, sum(NVL(TOTAL_CT,0))/24 as actual, decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY) as factory, /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月'
/*按季度*/ TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度'
/*按天*/ TO_CHAR(PERIODDATE,'mm/dd')
as periodDate from DWR_PRODUCT_CT_FIDS WHERE
PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
and FACTORY in ('ARRAY','CELL','SL','OC')
and PRODUCTID in
#{productId}
and ( ( FACTORY IN ( 'ARRAY','CELL','SL') and PRODUCTTYPE IN ('CS','MP','CM') ) OR ( FACTORY = 'OC' and PRODUCTTYPE IN ('ZP20','ZP24','ZP10') ) ) GROUP BY /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月',
TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度',
TO_CHAR(PERIODDATE,'mm/dd'),
decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY)

SELECT * from DWR_PRODUCT_CT_FIDS where FACTORY = #{FACTORY,jdbcType=VARCHAR} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} AND PERIODDATE = #{PERIODDATE,jdbcType=DATE} AND
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR}
PRODUCTTYPE is null

mapper.update:
UPDATE DWR_PRODUCT_CT_FIDS SET CT_TARGET = #{CT_TARGET,jdbcType=DOUBLE} , TOTAL_CT = #{TOTAL_CT,jdbcType=DOUBLE} , TOTAL_GLS_QTY = #{TOTAL_GLS_QTY,jdbcType=DOUBLE}, INTERFACE_TIME = sysdate WHERE FACTORY = #{FACTORY,jdbcType=VARCHAR} and PERIODDATE = #{PERIODDATE,jdbcType=DATE} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} AND
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR}
PRODUCTTYPE is null
