mapper.select:
select
    avg(TT_TARGET) as target,
    sum(TOTAL_TT)/sum(TOTAL_GLS_QTY) as actual,
    PRODUCTID as productId
from DWR_PRODUCT_TT_FIDS
WHERE
    FACTORY IN

        #{factory}

    AND PRODUCTID IN

        #{productId}


  and PERIODDATE >= #{beginDate}
  and PERIODDATE <= #{endDate}

group by PRODUCTID

SELECT
  sum(TT_TARGET) as target,
  sum(TOTAL_TT)/sum(TOTAL_GLS_QTY) as total,
  to_char(PERIODDATE,'HH24')||':00' as periodDate,
  PRODUCTID
FROM
  DWR_PRODUCT_TT_FIDS
WHERE
    FACTORY IN

        #{factory}

    and PRODUCTTYPE IN

        #{productType}

    and PRODUCTID=#{productId}

    and PERIODDATE >= #{beginDate}
    and PERIODDATE <= #{endDate}

group by PRODUCTID,to_char(PERIODDATE,'HH24')||':00'
