mapper.select:
select sum(nvl(TOTAL_TT,0)) as sumTotalTT, sum(nvl(TOTAL_GLS_QTY,0)) as sumTotalGlsQty, avg(TT_TARGET) as target, '${productId}' as productId from DWR_PRODUCT_TT_FIDS tt WHERE tt.FACTORY IN
#{factory}
and tt.EQP_ID IN
#{eqpId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
group by '${productId}'

select stt/sqty as r,EQP_ID from (

    select sum(TOTAL_TT) as stt , sum(TOTAL_GLS_QTY) as sqty,EQP_ID from
    DWR_PRODUCT_TT_FIDS
    where FACTORY IN

            #{factory}

    and  EQP_ID IN

            #{eqpId}


    and PERIODDATE >= #{beginDate}
    and PERIODDATE <= #{endDate}

    and
    (
        (
            FACTORY IN ( 'ARRAY','CELL','CF','SL')
            and PRODUCTTYPE IN ('CS','MP','CM')
        )
      OR
        (
            FACTORY = 'OC'
            and PRODUCTTYPE IN ('ZP21','ZP24','ZP10')
        )
    )
    group by EQP_ID

)
where sqty != 0 and stt !=0
order by r

SELECT sum(nvl(TOTAL_TT,0)) as sumTotalTT, sum(nvl(TOTAL_GLS_QTY,0)) as sumTotalGlsQty, avg(NVL(TT_TARGET,0)) as target, to_char(PERIODDATE,'MM/DD') as periodDate, '${productId}' as PRODUCTID FROM DWR_PRODUCT_TT_FIDS tt WHERE tt.FACTORY IN
#{factory}
and tt.EQP_ID IN
#{eqpId}
and ( ( FACTORY IN ( 'ARRAY','CELL','CF','SL') and PRODUCTTYPE IN ('CS','MP','CM') ) OR ( FACTORY = 'OC' and PRODUCTTYPE IN ('ZP21','ZP24','ZP10') ) )
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
group by '${productId}',to_char(PERIODDATE,'MM/DD')
