mapper.select:
select avg(TT_TARGET) as target, avg(NVL(TOTAL_TT,0))/avg(TOTAL_GLS_QTY) as actual, gg.BIG_EQP_TYPE as productId from DWR_PRODUCT_TT_FIDS tt join DWR_EQPTYPE_MASTER_FIDS gg on gg.EQP_TYPE = tt.EQP_TYPE WHERE tt.FACTORY IN
#{factory}
and gg.BIG_EQP_TYPE IN
#{productId}
and ( ( tt.FACTORY IN ( 'ARRAY','CELL','CF','SL') and tt.PRODUCTTYPE IN ('CS','MP','CM','DM') ) OR ( tt.FACTORY = 'OC' and tt.PRODUCTTYPE IN ('ZP21','ZP24','ZP10') ) )
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
group by gg.BIG_EQP_TYPE

SELECT avg(NVL(TT_TARGET,0)) as target, avg(NVL(TOTAL_TT,0))/avg(TOTAL_GLS_QTY) as total, to_char(PERIODDATE,'MM/DD') as periodDate, gg.BIG_EQP_TYPE as PRODUCTID FROM DWR_PRODUCT_TT_FIDS tt join DWR_EQPTYPE_MASTER_FIDS gg on gg.EQP_TYPE = tt.EQP_TYPE WHERE tt.FACTORY IN
#{factory}
and ( ( tt.FACTORY IN ( 'ARRAY','CELL','CF','SL') and tt.PRODUCTTYPE IN ('CS','MP','CM','DM') ) OR ( tt.FACTORY = 'OC' and tt.PRODUCTTYPE IN ('ZP21','ZP24','ZP10') ) ) and gg.BIG_EQP_TYPE=#{productId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
group by gg.BIG_EQP_TYPE,to_char(PERIODDATE,'MM/DD')
