mapper.select:
select avg(TT_TARGET) as target, avg(NVL(TOTAL_TT,0))/avg(TOTAL_GLS_QTY) as actual, gg.GROUPNAME as productId from DWR_PRODUCT_TT_FIDS tt join FACTORY_GROUP_EQP_MAPPING gg on gg.EQP_ID = tt.EQP_ID WHERE tt.FACTORY IN
#{factory}
and gg.GROUPNAME IN
#{productId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
group by gg.GROUPNAME

SELECT avg(NVL(TT_TARGET,0)) as target, avg(NVL(TOTAL_TT,0))/avg(TOTAL_GLS_QTY) as total, to_char(PERIODDATE,'HH24')||':00' as periodDate, gg.GROUPNAME as PRODUCTID FROM DWR_PRODUCT_TT_FIDS tt join FACTORY_GROUP_EQP_MAPPING gg on gg.EQP_ID = tt.EQP_ID WHERE tt.FACTORY IN
#{factory}
and PRODUCTTYPE IN
#{productType}
and gg.GROUPNAME=#{productId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
group by gg.GROUPNAME,to_char(PERIODDATE,'HH24')||':00'
