mapper.select:
SELECT STEPID as stepId, sum(WIP_GLS_QTY) as quantity, sum(STORE_MAX) as storeMax, sum(STORE_MIN) as storeMin FROM DWR_WIP_GLS_FIDS WHERE FACTORY=#{factory} AND STEPID IN
#{stepId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
GROUP BY STEPID

select sum(STORE_MAX) as storeMax, sum(STORE_MIN) as storeMin, sum(WIP_GLS_QTY) as quantity, STEPID as setepId, FACTORY as factory from DWR_WIP_GLS_FIDS WHERE FACTORY IN
#{factory}
AND STEPID IN
#{stepId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
GROUP BY STEPID,FACTORY
