mapper.select:
SELECT STEPID as stepId, sum(WIP_GLS_QTY) as quantity, sum(STORE_MAX) as storeMax, sum(STORE_MIN) as storeMin FROM DWR_WIP_GLS_FIDS WHERE FACTORY IN
#{factory}
and PRODUCTTYPE IN
#{productType}
AND STEPID IN
#{stepId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
GROUP BY STEPID

select
    sum(STORE_MAX) as storeMax,
    sum(STORE_MIN) as storeMin,
    sum(WIP_GLS_QTY) as quantity,
    /*STEPID as setepId,*/
    decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY) as factory,
    TO_CHAR(PERIODDATE,'HH24')||':00' as dataDate
from DWR_WIP_GLS_FIDS
WHERE
    FACTORY IN

        #{factory}

    and PRODUCTTYPE IN

        #{productType}



      and PERIODDATE >= #{beginDate}
      and PERIODDATE <= #{endDate}

GROUP BY
  /*STEPID,*/
  decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY),
  TO_CHAR(PERIODDATE,'HH24')||':00'
