mapper.select:
SELECT d.big_eqp_type as bigEqpType, SUM(t.wip_gls_qty)/1000 as wipGlsQty FROM dwr_wip_gls_fids t INNER JOIN dwr_wip_master d ON t.stepid = d.op_no and t.factory = d.factory WHERE t.factory IN
#{factory}
and ( ( t.FACTORY = 'SL' and t.PRODUCTTYPE IN ('CS','MP','CM') ) OR ( t.FACTORY = 'OC' and t.PRODUCTTYPE IN ('ZP20','ZP24','ZP10') ) OR ( t.FACTORY = 'ARRAY' and t.PRODUCTTYPE IN ('CS','CM','MP') ) OR ( t.FACTORY = 'CELL' and t.PRODUCTTYPE IN ('CS','CM','MP') ) ) AND d.big_eqp_type in
#{bigEqpType}
AND t.perioddate >= #{beginDate} and t.perioddate <= #{endDate}
group by d.big_eqp_type

SELECT SUM(t.wip_gls_qty)/1000 as wipGlsQty FROM dwr_wip_gls_fids t WHERE t.factory IN
#{factory}
and ( ( FACTORY = 'SL' and PRODUCTTYPE IN ('CS','MP','CM') ) OR ( FACTORY = 'OC' and PRODUCTTYPE IN ('ZP20','ZP24','ZP10') ) OR ( FACTORY = 'ARRAY' and PRODUCTTYPE IN ('CS','CM','MP') ) OR ( FACTORY = 'CELL' and PRODUCTTYPE IN ('CS','CM','MP') ) )
AND t.perioddate >= #{beginDate} and t.perioddate <= #{endDate}

SELECT sum(WIP_GLS_QTY)/1000 as wip, TO_CHAR(PERIODDATE,'MM')||'月' as dateTime FROM DWR_WIP_GLS_FIDS WHERE PERIODDATE in
#{periodDate}
and ( ( FACTORY = 'SL' and PRODUCTTYPE IN ('CS','MP','CM') ) OR ( FACTORY = 'OC' and PRODUCTTYPE IN ('ZP20','ZP24','ZP10') ) OR ( FACTORY = 'ARRAY' and PRODUCTTYPE IN ('CS','CM','MP') ) OR ( FACTORY = 'CELL' and PRODUCTTYPE IN ('CS','CM','MP') and PRODUCTID LIKE 'D%' ) ) and FACTORY in
#{factory}
group by TO_CHAR(PERIODDATE,'MM')||'月'

SELECT STEPID as stepId, STEPDESC as stepDesc, sum(WIP_GLS_QTY) as quantity, sum(STORE_MAX) as storeMax, sum(STORE_MIN) as storeMin FROM DWR_WIP_GLS_FIDS WHERE FACTORY IN
#{factory}
AND STEPID IS NOT NULL and ( ( FACTORY IN ( 'ARRAY','CELL','CF','SL') and PRODUCTTYPE IN ('CS','MP','CM') ) OR ( FACTORY = 'OC' and PRODUCTTYPE IN ('ZP20','ZP24','ZP10') ) ) AND STEPID IN
#{stepId}
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
GROUP BY STEPID,STEPDESC

select sum(STORE_MAX) as storeMax, sum(STORE_MIN) as storeMin, sum(WIP_GLS_QTY) as quantity, decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY) as factory, TO_CHAR(PERIODDATE,'HH24')||':00' as dataDate from DWR_WIP_GLS_FIDS WHERE FACTORY IN
#{factory}
AND STEPID IS NOT NULL and ( ( FACTORY in ('ARRAY','CELL','SL') and PRODUCTTYPE IN ('CS','MP','CM') AND PRODUCTID IN ('D41A','D51A','D52A','D53A') ) OR ( FACTORY IN ('CF') and PRODUCTTYPE IN ('CS','MP','CM') AND PRODUCTID IN ('C41A','C51A','C52A') ) OR ( FACTORY in ('OC') and PRODUCTTYPE IN ('ZP20','ZP24','ZP10') AND PRODUCTID IN ('A1CC495PU1L01','A1CC575PU1L01','A1CC575PU2L01','A1CC575PU3L01') ) )
and PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
GROUP BY decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY), TO_CHAR(PERIODDATE,'HH24')||':00'
