mapper.select:
SELECT * from DWS_PRODUCT_INPUT_FIDS where FACTORY = #{FACTORY,jdbcType=VARCHAR} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} and PERIODDATE = #{PERIODDATE,jdbcType=DATE} and
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR}
PRODUCTTYPE is null

mapper.update:
UPDATE DWS_PRODUCT_INPUT_FIDS SET PLAN_INPUT_GLS_QTY = #{PLAN_INPUT_GLS_QTY,jdbcType=DOUBLE} , ACTUAL_INPUT_GLS_QTY = #{ACTUAL_INPUT_GLS_QTY,jdbcType=DOUBLE}, INTERFACE_TIME = sysdate WHERE FACTORY = #{FACTORY,jdbcType=VARCHAR} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} and PERIODDATE = #{PERIODDATE,jdbcType=DATE} and
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR}
PRODUCTTYPE is null
