mapper.select:
select decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY) as factory, sum(OUTPUT_GLS_QTY) as outputGls, sum(SCRAP_GLS_QTY) as scrapGls, sum(INPUT_PNL_QTY) as inputPnl, sum(OUTPUT_PNL_QTY) as outputPnl, /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月'
/*按季度*/ TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度'
/*按天*/ TO_CHAR(PERIODDATE,'mm/dd')
as periodDate from DWS_PRODUCT_LINE_YIELD_FIDS WHERE
PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
AND FACTORY IN
#{factory}
and PRODUCTID IN
#{productId}
and ( ( FACTORY in ('ARRAY','CELL') and PRODUCTTYPE IN ('CS','MP','CM') AND PRODUCTID IN ('D41A','D51A','D52A','D53A') ) OR ( FACTORY IN ('CF') and PRODUCTTYPE IN ('CS','MP','CM') AND PRODUCTID IN ('C41A','C51A','C52A') ) OR ( FACTORY in ('OC') and PRODUCTTYPE IN ('ZP21','ZP24','ZP10') AND PRODUCTID IN ('A1CC495PU1L01','A1CC575PU1L01','A1CC575PU2L01','A1CC575PU3L01') ) OR ( FACTORY in ('SL') and PRODUCTTYPE IN ('CS','MP','CM') AND PRODUCTID IN ('D41A','D51A','D52A','D53A') ) )
GROUP BY /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月',
TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度',
TO_CHAR(PERIODDATE,'mm/dd'),
decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY)

