UPDATE DWS_PRODUCT_LINE_YIELD_FIDS SET OUTPUT_GLS_QTY = #{OUTPUT_GLS_QTY,jdbcType=DOUBLE} , SCRAP_GLS_QTY = #{SCRAP_GLS_QTY,jdbcType=DOUBLE} , INPUT_PNL_QTY = #{INPUT_PNL_QTY,jdbcType=DOUBLE}, OUTPUT_PNL_QTY = #{OUTPUT_PNL_QTY,jdbcType=DOUBLE} , INTERFACE_TIME = sysdate WHERE FACTORY = #{FACTORY,jdbcType=VARCHAR} and PERIODDATE = #{PERIODDATE,jdbcType=DATE} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} and
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR}
PRODUCTTYPE is null

