SELECT decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY) as factory, sum(OUTPUT_GLS_QTY) as outputGls, sum(SCRAP_GLS_QTY) as scrapGls, sum(INPUT_PNL_QTY) as inputPnl, sum(OUTPUT_PNL_QTY) as outputPnl FROM DWS_PRODUCT_LINE_YIELD_FIDS WHERE FACTORY IN
#{factory}
and ( ( FACTORY in ('ARRAY','CELL','SL') and PRODUCTTYPE IN ('CS','MP','CM') AND PRODUCTID IN ('D41A','D51A','D52A','D53A') ) OR ( FACTORY IN ('CF') and PRODUCTTYPE IN ('CS','MP','CM') AND PRODUCTID IN ('C41A','C51A','C52A') ) OR ( FACTORY in ('OC') and PRODUCTTYPE IN ('ZP21','ZP24','ZP10') AND PRODUCTID IN ('A1CC495PU1L01','A1CC575PU1L01','A1CC575PU2L01','A1CC575PU3L01') ) )
and PERIODDATE >= #{startDate} and PERIODDATE <= #{endDate}
GROUP BY decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY)

