mapper.select:
select FACTORY as factory, sum(POL_INPUT) as polInput, sum(POL_OUTPUT_A) as polOutputA, sum(POL_OUTPUT_FA) as polOutputFA, sum(MBI_RJ_OUTPUT_A) as mbiRjOutputA, sum(MBI_RJ_OUTPUT_FA) as mbiRjOutputFA, sum(POL_RW_OUTPUT_A) as polRwOutputA, sum(POL_RW_OUTPUT_FA) as polRwOutputFA, sum(IOB_INPUT) as iobInput, sum(IOB_EI_OUTPUT_A) as iobEiOutputA, sum(IOB_EI_OUTPUT_FA) as iobEiOutputFA, sum(IOB_EI_OUTPUT_B_RW_A) as iobEiOutputBRwA, sum(IOB_EI_OUTPUT_B_RW_FA) as iobEiOutputBRwFA, sum(IOB_EI_OUTPUT_D_RW_A) as iobEiOutputDRwA, sum(IOB_EI_OUTPUT_D_RW_FA) as iobEiOutputDRwFA, /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月'
/*按季度*/ TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度'
/*按天*/ TO_CHAR(PERIODDATE,'mm/dd')
as periodDate from DWS_PRODUCT_OC_YIELD_FIDS WHERE
PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
AND FACTORY=#{factory,jdbcType=VARCHAR}
AND PRODUCTID=#{productId,jdbcType=VARCHAR}
GROUP BY /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月',
TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度',
TO_CHAR(PERIODDATE,'mm/dd'),
FACTORY
