mapper.select:
select TARGET_VAL/1000 from
DWR_OUTPUT_TARGET_DAY
where FACTORY = #{factory}

and PERIODDATE >= #{sDate}
and PERIODDATE <= #{eDate}

and rownum=1

select TARGET_VAL/1000 from
DWR_OUTPUT_TARGET_MON
where FACTORY = #{factory}

and PERIODDATE >= #{sDate}
and PERIODDATE <= #{eDate}

and rownum=1

select sum(PLAN_OUTPUT_PNL_QTY) as plan, sum(ACTUAL_OUTPUT_PNL_QTY) as actual,FACTORY, /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月'
/*按季度*/ TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度'
/*按天*/ TO_CHAR(PERIODDATE,'mm/dd')
as periodDate from DWS_PRODUCT_OUTPUT_FIDS WHERE
PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
and ( ( FACTORY = 'SL' and PRODUCTTYPE IN ('CS','MP','CM') ) OR ( FACTORY = 'OC' and PRODUCTTYPE IN ('ZP20','ZP24','ZP10') ) OR ( FACTORY = 'ARRAY' and PRODUCTTYPE IN ('CS','CM','MP') ) OR ( FACTORY = 'CELL' and PRODUCTTYPE IN ('CS','CM','MP') ) ) AND FACTORY IN
#{factory}
and PRODUCTID in
#{productId}
GROUP BY /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月',
TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度',
TO_CHAR(PERIODDATE,'mm/dd'),
FACTORY

select sum(PLAN_OUTPUT_PNL_QTY)/1000 as plan, sum(ACTUAL_OUTPUT_PNL_QTY)/1000 as actual, /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月'
/*按季度*/ TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度'
/*按天*/ TO_CHAR(PERIODDATE,'mm/dd')
as dateTime from DWS_PRODUCT_OUTPUT_FIDS WHERE
PERIODDATE >= #{beginDate} and PERIODDATE <= #{endDate}
and ( ( FACTORY = 'SL' and PRODUCTTYPE IN ('CS','MP','CM') ) OR ( FACTORY = 'OC' and PRODUCTTYPE IN ('ZP20','ZP24','ZP10') ) OR ( FACTORY = 'ARRAY' and PRODUCTTYPE IN ('CS','CM','MP') ) OR ( FACTORY = 'CELL' and PRODUCTTYPE IN ('CS','CM','MP') and (PRODUCTID LIKE 'D%' ) ) ) AND FACTORY IN
#{factory}
GROUP BY /*按月*/ TO_CHAR(PERIODDATE,'MM')||'月'
TO_CHAR(PERIODDATE,'yyyy') ||'年' || TO_CHAR(PERIODDATE,'q') ||'季度'
TO_CHAR(PERIODDATE,'mm/dd')

SELECT sum(ACTUAL_OUTPUT_PNL_QTY) as outputNum FROM DWS_PRODUCT_OUTPUT_FIDS WHERE PRODUCTID IN
#{productId}
and FACTORY = 'OC' and ( ( FACTORY = 'SL' and PRODUCTTYPE IN ('CS','MP','CM') ) OR ( FACTORY = 'OC' and PRODUCTTYPE IN ('ZP20','ZP24','ZP10') ) )
and PERIODDATE >= #{startDate} and PERIODDATE <= #{endDate}

SELECT * from DWS_PRODUCT_OUTPUT_FIDS where FACTORY = #{FACTORY,jdbcType=VARCHAR} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} and PERIODDATE = #{PERIODDATE,jdbcType=DATE} and
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR}
PRODUCTTYPE is null

mapper.update:
UPDATE DWS_PRODUCT_OUTPUT_FIDS SET PLAN_OUTPUT_PNL_QTY = #{PLAN_OUTPUT_PNL_QTY,jdbcType=DOUBLE} , ACTUAL_OUTPUT_PNL_QTY = #{ACTUAL_OUTPUT_PNL_QTY,jdbcType=DOUBLE}, INTERFACE_TIME = sysdate WHERE FACTORY = #{FACTORY,jdbcType=VARCHAR} and PERIODDATE = #{PERIODDATE,jdbcType=DATE} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} and
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR,jdbcType=VARCHAR}
PRODUCTTYPE is null
