mapper.select:
select
    decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY) as factory,
    TO_CHAR(PERIODDATE,'HH24')||':00' as dataDate,
    sum(ACTUAL_OUTPUT_PNL_QTY) as amount
from DWS_PRODUCT_OUTPUT_FIDS_H
WHERE

       PERIODDATE >= #{beginDate}
       and PERIODDATE <= #{endDate}

    and PRODUCTID in

        #{productId}

    and FACTORY IN

        #{factory}

    and PRODUCTTYPE IN

        #{productType}

GROUP BY
    decode(FACTORY,'SL','SL-OC','OC','SL-OC',FACTORY),TO_CHAR(PERIODDATE,'HH24')||':00'

SELECT * from DWS_PRODUCT_OUTPUT_FIDS_H where FACTORY = #{FACTORY,jdbcType=VARCHAR} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} and PERIODDATE = #{PERIODDATE,jdbcType=DATE} and
PRODUCTTYPE is null
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR}

mapper.update:
UPDATE DWS_PRODUCT_OUTPUT_FIDS_H SET PLAN_OUTPUT_PNL_QTY = #{PLAN_OUTPUT_PNL_QTY} , ACTUAL_OUTPUT_PNL_QTY = #{ACTUAL_OUTPUT_PNL_QTY}, INTERFACE_TIME = sysdate WHERE FACTORY = #{FACTORY,jdbcType=VARCHAR} and PRODUCTID = #{PRODUCTID,jdbcType=VARCHAR} and PERIODDATE = #{PERIODDATE,jdbcType=DATE} and
PRODUCTTYPE is null
PRODUCTTYPE = #{PRODUCTTYPE,jdbcType=VARCHAR}
