mapper.select:
select
    sum(TOTALNUM) as totalNum,
    /*按月*/
        TO_CHAR(DATADATE,'MM')||'月'

    /*按天*/
        TO_CHAR(DATADATE,'mm/dd')

    /*小时*/
        TO_CHAR(DATADATE,'HH24')||':00'

    as dataDate
from ELECEVERYHOURDATA
WHERE

       DATADATE >= #{beginDate}
      and DATADATE <= #{endDate}


        AND PLACE=#{place,jdbcType=VARCHAR}

GROUP BY
    /*按月*/
        TO_CHAR(DATADATE,'MM')||'月'


        TO_CHAR(DATADATE,'mm/dd')

    /*小时*/
        TO_CHAR(DATADATE,'HH24')||':00'

select
    sum(TOTALNUM) as totalNum,
    PLACETYPE as placeType,
    /*按月*/
        TO_CHAR(DATADATE,'MM')||'月'

    /*按天*/
        TO_CHAR(DATADATE,'mm/dd')

    /*小时*/
        TO_CHAR(DATADATE,'HH24')||':00'

    as dataDate
from ELECEVERYHOURDATA
WHERE

       DATADATE >= #{beginDate}
      and DATADATE <= #{endDate}

GROUP BY
    placeType,
    /*按月*/
        TO_CHAR(DATADATE,'MM')||'月'


        TO_CHAR(DATADATE,'mm/dd')

    /*小时*/
        TO_CHAR(DATADATE,'HH24')||':00'
