mapper.select:
select GASNAME as gasName, avg(SPEED) as speed, TO_CHAR(DATADATE,'mi')||':00' as periodDate, TO_CHAR(DATADATE,'mi') || ':' || case when To_number(TO_CHAR(DATADATE,'ss'))<15 then '00' when To_number(TO_CHAR(DATADATE,'ss'))<30 then '15' when To_number(TO_CHAR(DATADATE,'ss'))<45 then '30' else '45' end as secondDate from GASREALTIMEDATA WHERE
DATADATE >= #{beginDate} and DATADATE <= #{endDate}
AND GASNAME=#{gasName,jdbcType=VARCHAR}
GROUP BY GASNAME, TO_CHAR(DATADATE,'mi') || ':' || case when To_number(TO_CHAR(DATADATE,'ss'))<15 then '00' when To_number(TO_CHAR(DATADATE,'ss'))<30 then '15' when To_number(TO_CHAR(DATADATE,'ss'))<45 then '30' else '45' end
