mapper.select:
select FACTORY as factory, avg(TEMPERATURE) as temperature, avg(HUMIDITY) as humidity, avg(CLEANLINESS) as cleanliness, TO_CHAR(DATADATE,'mi')||':00' as periodDate,
TO_CHAR(DATADATE,'mi') || ':' || case when To_number(TO_CHAR(DATADATE,'ss'))<15 then '00' when To_number(TO_CHAR(DATADATE,'ss'))<30 then '15' when To_number(TO_CHAR(DATADATE,'ss'))<45 then '30'
else '45' end as secondDate from HUMITUREDATA WHERE
DATADATE >= #{beginDate} and DATADATE <= #{endDate}
AND FACTORY=#{factory,jdbcType=VARCHAR}
AND PLACE=#{place,jdbcType=VARCHAR}
AND EQUIPMENT=#{equipment,jdbcType=VARCHAR}
GROUP BY FACTORY, TO_CHAR(DATADATE,'mi')||':00',
TO_CHAR(DATADATE,'mi') || ':' || case when To_number(TO_CHAR(DATADATE,'ss'))<15 then '00' when To_number(TO_CHAR(DATADATE,'ss'))<30 then '15' when To_number(TO_CHAR(DATADATE,'ss'))<45 then '30'
else '45' end
