mapper.select:
SELECT
    SUM (TotalNum) as totalNum,
    /*按月*/
        TO_CHAR(DataDate,'MM')||'月'

    /*按天*/
        TO_CHAR(DataDate,'mm/dd')

    as periodDate
FROM NatgasEveryDayData
WHERE

       DataDate >= #{beginDate}
      and DataDate <= #{endDate}

    AND GasType=#{gasType,jdbcType=VARCHAR}
GROUP BY
    /*按月*/
        TO_CHAR(DataDate,'MM')||'月'


        TO_CHAR(DataDate,'mm/dd')

SELECT * from NatgasEveryDayData
where GASTYPE = #{GASTYPE} and
DATADATE = #{DATADATE}

SELECT GASTYPE as gasType, sum(TOTALNUM) as totalNum, TO_CHAR(DATADATE,'mm/dd') as dataDate FROM NatgasEveryDayData WHERE TO_CHAR(DATADATE,'mm/dd')=TO_CHAR(sysdate,'mm/dd') AND GASTYPE IN
#{gasType}
GROUP BY GASTYPE,TO_CHAR(DATADATE,'mm/dd')

SELECT GASTYPE as gasType, sum(TOTALNUM) as totalNum, TO_CHAR(DATADATE,'MM')||'月' as dataDate FROM NatgasEveryDayData WHERE TO_CHAR(DATADATE,'mm')=TO_CHAR(sysdate,'mm') AND GASTYPE IN
#{gasType}
GROUP BY GASTYPE,TO_CHAR(DATADATE,'MM')||'月'
