mapper.select:
SELECT SUM (speed) as speed, periodDate , dataDate FROM ( SELECT GasType,Place, AVG(Speed) as speed, TO_CHAR(DataDate,'mi')||':00' as periodDate,
TO_CHAR(DataDate,'mi') || ':' || case when To_number(TO_CHAR(DataDate,'ss'))<15 then '00' when To_number(TO_CHAR(DataDate,'ss'))<30 then '15' when To_number(TO_CHAR(DataDate,'ss'))<45 then '30'
else '45' end as dataDate FROM NatgasRealTimeData WHERE
DataDate >= #{beginDate} and DataDate <= #{endDate}
AND GasType=#{gasType,jdbcType=VARCHAR}
AND Place=#{place,jdbcType=VARCHAR}
GROUP BY GasType,Place, TO_CHAR(DataDate,'mi')||':00',
TO_CHAR(DataDate,'mi') || ':' || case when To_number(TO_CHAR(DataDate,'ss'))<15 then '00' when To_number(TO_CHAR(DataDate,'ss'))<30 then '15' when To_number(TO_CHAR(DataDate,'ss'))<45 then '30'
else '45' end ) GROUP BY GasType,Place, periodDate , dataDate
