mapper.select:
SELECT
    SUM(TotalNum) as totalNum,
    /*按月*/
        TO_CHAR(DataDate,'MM')||'月'

    /*按天*/
        TO_CHAR(DataDate,'mm/dd')

    as dataDate
FROM PureWaterEveryDayData
WHERE

        DataDate >= #{beginDate}
        and DataDate <= #{endDate}


        AND Type=#{waterType,jdbcType=VARCHAR}

GROUP BY
    /*按月*/
        TO_CHAR(DataDate,'MM')||'月'


        TO_CHAR(DataDate,'mm/dd')
